import './App.css';
import React, { createContext, useState, useEffect, useRef } from "react";
import { Route, Routes, Navigate, useNavigate } from "react-router-dom";
import Banner from "./Components/Banner/banner.js";
import Games from "./Components/Games/games.js";
import Home from "./Components/Home/home.js";
import Login from "./Components/Logging/login.js";
import Signup from "./Components/Logging/signup.js";
import Profile from "./Components/Profile/profile.js";
import Taproom from "./Components/Taproom/taproom.js";

const LoggedInContext = createContext();
const UserContext = createContext();

export { LoggedInContext, UserContext };

function App() {

  const [isLoggedIn, setIsLoggedIn] = useState("unchecked");
  const [user, _setUser] = useState({});
  const [logNav, setLogNav] = useState(false);
  const navigate = useNavigate();
  const userRef = useRef(user);

  function setUser(data) {
    _setUser(data);
    userRef.current = data;
  };

  useEffect(() => {
    async function fetchData() {
      const response = await fetch("/me");
      if (response.ok) {
        const usr = await response.json();
        setUser(usr);
        setIsLoggedIn(true);
       } else {
        setIsLoggedIn(false);
      }
    };
    fetchData();
  }, [logNav]);

  function onLogin(user) {
    async function fetchData() {
      const response = await fetch("/me");
      if (response.ok) {
        const user = await response.json();
        setUser(user);
        setIsLoggedIn(true);
       } else {
        setIsLoggedIn(false);
      }
    };
    setUser(user);
    setIsLoggedIn(true);
    setLogNav(true);
    fetchData();
    navigate("/home");
  };

  function logout() {
    navigate("/home");
    setUser();
    setIsLoggedIn(false);
    setLogNav(false);
  };

  return (
    <LoggedInContext.Provider value={isLoggedIn}>
      <UserContext.Provider value={userRef.current}>
        <div className="App">
          <Banner />
          <div id="content_panel">
            <Routes>
              <Route path="/games" element={<Games />} />
              <Route path="/login" element={<Login
                onLogin={onLogin}
              />} />
              <Route path="/signup" element={<Signup
                onLogin={onLogin}
              />} />
              <Route path="/profile" element={<Profile
                setUser={setUser}
                logout={logout}
              />} />
              <Route path="/taproom" element={<Taproom />} />
              <Route path="/home" element={<Home />} />
              <Route path="/" element={<Navigate to="/home" />} />
            </Routes>
          </div>
        </div>
      </UserContext.Provider>
    </LoggedInContext.Provider>
  );
};

export default App;
